import { LOAD_DROPBOX_DATA } from "./types"
import axios from 'axios'
import { Dropbox } from "dropbox"

export const loadDropboxData = () => async dispatch => {
    var dbx = new Dropbox({ 
        accessToken: 'sl.BAuDa9rsvD6TVigfwU7jc2bs9v9-BYtPVBdaMnPUKisLFzxpCQqC4nI7lZ5KA0oOvSWixLQH3CTPb17_-6eQgxSoUIJV9qKWILaQ2WGfW1yVqrjhpA3j8SlYKlCEGgmV1na3ISg4gKe_',
        axios
      });
    try{
        let usResponse = await axios.get('https://api.covidtracking.com/v1/us/current.json')
        let statesResponse = await axios.get('https://api.covidtracking.com/v1/states/current.json')
        dispatch({
            type: LOAD_DROPBOX_DATA,
            data: {
                usData: usResponse.data[0],
                stateData: statesResponse.data
            }
        })
    } catch(err){
        console.log('err:', err)
    }
}
