import React, {useEffect, useState} from "react";
import {useDispatch, useSelector} from 'react-redux';
import { loadDropboxData } from "./actions";
import ImgView from "./components/ImgView";
import { Dropbox } from "dropbox"


function App() {
  const dispatch = useDispatch();
  const fileArray = useSelector(state => state.dropbox.dropboxFileArray)
  const imageArray = useSelector(state => state.dropbox.thumbnailArray)
  const [imgThumbnail, setImgThumbnail] = useState(null);

  useEffect(() => {
    dispatch(loadDropboxData())
  }, [])


  const handleGetImg = (path) => {
    try{
      let dbx = new Dropbox({ 
          accessToken: 'sl.BAuriOtPpEMA_28LhjbiaXgi1eiVlBrzQtVacffvsUTSLpEDBaqLrR3u8dh5r9j__LZ0MlS_Bhx5Jbq3VWmmYPyp3qEsKG9dJoQWC8lvQ6n4Q7C0AqMLTnGlO6VCuMAQWd45kbcspzvF'
      });
      dbx.filesGetThumbnailBatch({
              entries :[
                {path:`${path}`,
                size: "w1024h768"}
            ]
      }).
      then(res => {
        console.log(res);
        setImgThumbnail(`data:image/jpeg;base64, ${res.result.entries[0].thumbnail}`)
      })
      .catch(function(error) {
          console.error(error);
      });
  } catch(err){
      console.log('err:', err)
  }
  };


  return (
    <>
    {fileArray.map(fileObj=>{
      return( 
        <>
        <div onClick={()=>handleGetImg(fileObj.path_lower)}>{fileObj.name}</div>
        </>
      )
    })}
    <div></div>
    {/* {imageArray.map((imageObj, index)=>{
      let thumbnail = `data:image/jpeg;base64, ${imageObj.thumbnail}`
      return (
        <>
          <img key={imageObj.metadata.name} src={thumbnail} alt={imageObj.metadata.name} />
          <br key={index}/>
        </>
      )
    })} */}
    </>
  )
}

export default App
