import { LOAD_DROPBOX_DATA} from "../actions/types"


const initialState = {
    dropboxImageArray: [],
    thumbnailArray
}

const dropboxReducer = (state=initialState, action) => {
    switch(action.type){
        case LOAD_DROPBOX_DATA:
            return {
                ...state,
                dropboxImageArray: action.data.filesList
            }
        default:
            return state
    }
}

export default dropboxReducer
